package basicjava;

public class Revision {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Arthmatic Operator
		int a= 5;
		int b= 7;
		System.out.println(a+b);
		System.out.println(a-b);	
		System.out.println(a/b);	
		System.out.println(a%b);
		
		
		//Relation operatior/ Comparision ---->True or False
		 System.out.println(9>5);  //True
		 System.out.println(9<5);  //False
		 System.out.println(9!=5); //False
		 System.out.println(9==9); //True
		 System.out.println(9==7); //False
		 System.out.println(9<=9); //True
		 System.out.println(9>=5); //True
		 
		 
		 //Logical Operator  

		 	//a) and--&& (if both True then print True otherwise false) 
		 System.out.println(9==9 && 9>=10);// True && False---> False
		 System.out.println(9<1 && 4>1);   // False && True---> False
		 System.out.println(9<=8 && 8>=9); // False && False--> False
		 System.out.println(5==5 && 5>2);  // True && True----> True
		 
		 	//b) Or---|| (if any one True then print True)
		 System.out.println(7<=8 || 0==0); // True || True---> True
		 System.out.println(7==7 || 5<4);  // True || False--> True
		 System.out.println(9<=7 || 5>=5); // False || True--> True
		 System.out.println(2<=0 || 0>=1); // False || False-> False
		 	 	
		 
		 	// Not !
		 System.out.println(!(9>=9));// False
		 System.out.println(!(9>=10));// True
		 System.out.println(!(5==5)); // False
		 System.out.println(!(5<=0)); // True
		 
		 
		 	//Conditional Statements
		 
		

		 
		
		 
		 
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
